mapper.select:
SELECT id, business_date, is_active, business_type, created_at
FROM business_days
WHERE id = #{id}

SELECT id, business_date, is_active, business_type, created_at
FROM business_days
WHERE business_date = #{date}

mapper.update:
UPDATE business_days
SET is_active = #{isActive},
    business_type = #{businessType}
WHERE id = #{id}
