mapper.select:
SELECT id, card_number, birthday, name, password, phone_number, created_at
FROM users
WHERE id = #{id}

SELECT id, card_number, birthday, name, password, phone_number, created_at
FROM users
WHERE card_number = #{cardNumber}

mapper.update:
UPDATE users
SET name = #{name},
    phone_number = #{phoneNumber},
    password = #{password}
WHERE id = #{id}
